import React, { useState } from 'react';
import { useEffect } from 'react';
import Button from "react-bootstrap/Button";
import 'bootstrap/dist/css/bootstrap.min.css';
import './network.css';
import { Modal } from 'react-bootstrap';

function SearchBar() {
  const [show, setShow] = useState(false);
  const [posts, setPosts] = useState([]);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPost = {
      product: e.target.product.value,
      quantity: e.target.quantity.value,
      qunits: e.target.qunits.value,
      desc: e.target.desc.value,
      Type: e.target.Type.value,
    }
    setPosts([...posts, newPost]);
    handleClose();
  }

  return (
    <div className="container-lg col-md-auto">
      <div className="container-fluid">
        <Button type="Button" className="btn btn-success" onClick={handleShow}>Create Post</Button>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Create a new post</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="product">Product</label>
                <input type="text" className="form-control" id="product" name="product" required />
              </div>
              <div className="form-group">
                <label htmlFor="quantity">Quantity</label><input type="number" className="form-control" id="quantity" name="quantity" required />
              </div>
              <div className="form-group">
                <label htmlFor="qunits">Quantity Units</label>
                <input type="text" className="form-control" id="qunits" name="qunits" required />
              </div>
              <div className="form-group">
                <label htmlFor="desc">Description</label>
                <textarea className="form-control" id="desc" name="desc" rows="3"></textarea>
              </div>
              <div className="form-group">
                <label htmlFor="Type">Type</label>
                <select className="form-control" id="Type" name="Type" required>
                  <option value="Sharing">Sharing</option>
                  <option value="Receiving">Receiving</option>
                </select>
              </div>
              <button type="submit" className="btn btn-primary">Submit</button>
            </form>
          </Modal.Body>
        </Modal>
      </div>
      <div className="container-md">
        {/* post tabs and post display */}
      </div>
    </div>
  )
}

export default SearchBar;
