import React, { useState } from "react";

import trackerCSS from "./tracker.module.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";

import * as d3Fetch from "d3-fetch";
import * as d3Scale from "d3-scale";
import * as d3Shape from "d3-shape";
import * as d3Selection from "d3-selection";
import * as d3Axis from "d3-axis";
import * as d3ScaleChromatic from "d3-scale-chromatic";

function Tracker() {
  return (
    <div className="container p-2">
      <p>
        <strong>Welcome back, {/* {{ request.session.FirstName }} */}</strong>
      </p>
      <div className="card">
        <div className="card-header">
          <h3>Enter tracker data:</h3>
        </div>
        <div className="card-body">
          <div id="form-wrapper">
            <Form
              className="tracker-data-entry"
              action=""
              method="post"
              id="tracker"
            >
              <Form.Group>
                {/* {% csrf_token %} */}
                <div className="row">
                  <div className={`col-md-auto form-floating`}>
                    <input
                      type="text"
                      id="description"
                      className={`form-control input-text ${trackerCSS["customised-input"]}`}
                      placeholder="Description"
                      name="description"
                    />
                    <label
                      htmlFor="description"
                      className={`form-label input-text`}
                    >
                      <span class={`${trackerCSS["my-input-text"]}`}>
                        Description:
                      </span>
                    </label>
                    <br />
                  </div>
                  <div className="col-md-auto">
                    <label htmlFor="category">Category</label>
                    <br />
                    <select
                      className="form-select"
                      id="category"
                      name="category"
                    >
                      <option>Fresh Produce</option>
                      <option>Metric #2</option>
                      <option>Metric #3</option>
                      <option>Metric #4</option>
                      <option>Metric #5</option>
                    </select>
                  </div>
                  <div className="col-md-auto form-floating">
                    <input
                      type={`text`}
                      id="quantity"
                      className={`form-control input-text ${trackerCSS["customised-input"]}`}
                      placeholder="Quantity"
                      name="quantity"
                    />
                    <label htmlFor="quantity">
                      <span class={`${trackerCSS["my-input-text"]}`}>
                        Quantity:
                      </span>
                    </label>
                    <br />
                  </div>
                  <div className="col-md-auto">
                    <label htmlFor="qunits">Units</label>
                    <br />
                    <select className="form-select" id="qunits" name="qunits">
                      <option>lbs</option>
                      <option>kgs</option>
                    </select>
                  </div>
                  <div className="col-auto">
                    <div className="row">
                      <div className="col-auto">
                        <label htmlFor="amount">Amount</label>
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-input"]}`}
                          id="clients"
                          name="clients"
                          value={0}
                          min={0}
                          // style={{ width: "10em" }}
                          onKeyUp={() => {}}
                          // onkeyup="calculateLandfill(); calculatePercent()
                        />
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-input"]}`}
                          id="animalFeed"
                          name="animalFeed"
                          value={0}
                          min={0}
                          onKeyUp={() => {}}
                          // onkeyup="calculateLandfill(); calculatePercent()"
                        />
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-input"]}`}
                          id="compost"
                          name="compost"
                          value={0}
                          min={0}
                          onKeyUp={() => {}}
                          // onkeyup="calculateLandfill(); calculatePercent()"
                        />
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-input"]}`}
                          id="partnet"
                          name="partnet"
                          value={0}
                          min={0}
                          onKeyUp={() => {}}
                          // onKeyUp={() => {calculateLandfill(); calculatePercent()}}
                          // onkeyup="calculateLandfill(); calculatePercent()"
                        />
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-input"]}`}
                          id="landfill"
                          name="landfill"
                          value={0}
                          min={0}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="col">
                    <div className="row">
                      <div className="col-auto">
                        <label htmlFor="percent">% Diverted to:</label>
                        <br />
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-smaller-input"]}`}
                          id="percentclients"
                          name="percentclients"
                          value={0}
                          min={0}
                          max={100}
                          onLoad={() => {}}
                          // onLoad={() => calculateLandfillPercent()}
                          // onload="calculateLandfillPercent()"
                          readonly
                        />
                      </div>
                      <div className="col d-flex align-items-center">
                        Clients
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-smaller-input"]}`}
                          id="percentanimalfeed"
                          name="percentanimalfeed"
                          value={0}
                          min={0}
                          max={100}
                          onLoad={() => {}}
                          // onLoad={() => calculateLandfillPercent()}
                          // onload="calculateLandfillPercent()"
                          readonly
                        />
                      </div>
                      <div className="col d-flex align-items-center">
                        Animal Feed
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-smaller-input"]}`}
                          id="percentcompost/fert"
                          name="percentcompost/fert"
                          value={0}
                          min={0}
                          max={100}
                          onLoad={() => {}}
                          // onLoad={() => calculateLandfillPercent()}
                          // onload="calculateLandfillPercent()"
                          readonly
                        />
                      </div>
                      <div className="col d-flex align-items-center">
                        Compost/Fertilizer
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-smaller-input"]}`}
                          id="percentpartnet"
                          name="percentpartnet"
                          value={0}
                          min={0}
                          max={100}
                          onLoad={() => {}}
                          // onLoad={() => calculateLandfillPercent()}
                          // onLoad="calculateLandfillPercent()"
                          readonly
                        />
                      </div>
                      <div className="col d-flex align-items-center">
                        Partner Network
                      </div>
                    </div>
                    <div className="row pb-2">
                      <div className="col-auto">
                        <input
                          type="number"
                          className={`form-control ${trackerCSS["customised-smaller-input"]}`}
                          id="percentlandfill"
                          name="percentlandfill"
                          value={100}
                          readonly
                        />
                      </div>
                      <div className="col d-flex align-items-center">
                        Landfill
                      </div>
                    </div>
                  </div>
                </div>
                <div className="row p-2">
                  <div className="d-flex justify-content-start">
                    <Button
                      variant="outline-success"
                      className={`${trackerCSS["save"]} btn btn-outline-success`}
                      id="submit"
                      type="submit"
                    >
                      Save
                    </Button>
                  </div>
                </div>
              </Form.Group>
            </Form>
          </div>
        </div>
      </div>

      <br />
      <br />

      <section id="section">
        <div className="card">
          <div className="card-header">
            <h3>Database</h3>
            <Button
              type="button"
              variant="outline-success"
              className="btn btn-outline-success"
              onClick={() => {}}
              // onClick={() => exportTableToCSV("data.csv")}
            >
              Export CSV
            </Button>
          </div>
          <div className="card-body">
            <Table striped bordered hover responsive style={{ width: "100%" }}>
              <thead>
                <tr>
                  <th>Description</th>
                  <th>Category</th>
                  <th>Quantity</th>
                  <th>Units</th>
                  <th>Clients</th>
                  <th>Animal Feed</th>
                  <th>Compost</th>
                  <th>Partner Network</th>
                  <th>Landfill</th>
                  <th></th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {/* {% for i in Object %} */}
                <tr>
                  <td>{/* {{i.0}} */}</td>
                  <td>{/* {{i.1}} */}</td>
                  <td>{/* {{i.2}} */}</td>
                  <td>{/* {{i.3}} */}</td>
                  <td>{/* {{i.4}} */}</td>
                  <td>{/* {{i.5}} */}</td>
                  <td>{/* {{i.6}} */}</td>
                  <td>{/* {{i.7}} */}</td>
                  <td>{/* {{i.8}} */}</td>
                  <td>
                    <form action="{% url 'tracker' %}" method="post">
                      {/* {% csrf_token %} */}
                      <Button
                        variant="danger"
                        className="btn btn-danger"
